#!/usr/bin/env python3
dna = ""
print(dna.count('A'))
print(dna.count('T'))
print(dna.count('C'))
print(dna.count('G'))
at_content = ((dna.count('A')+dna.count('T'))/len(dna))*100
print(at_content)
gc_content = ((dna.count('G')+dna.count('C'))/len(dna))*100
print(gc_content)
print(f' The AT content of this sequence is {at_content:.2f} and the GC content of this sequence is {gc_content:.2f}')
